#!/bin/sh\nset -e\n\n# Chờ PostgreSQL sẵn sàng (có thể thêm logic phức tạp hơn nếu cần)\nuntil PGPASSWORD=$PGPASSWORD psql -h "$PGHOST" -U "$PGUSER" -d "$PGDATABASE" -p "$PGPORT" -c '\q'; do\n  >&2 echo "Postgres (Accounting DB) is unavailable - sleeping"\n  sleep 1\ndone\n\n>&2 echo "Postgres (Accounting DB) is up - executing command"\n\n# Thực thi các file .sql trong /schema_files theo thứ tự tên file\n# Chúng ta sẽ chỉ định file schema kế toán cụ thể\nPGPASSWORD=$PGPASSWORD psql -v ON_ERROR_STOP=1 --host "$PGHOST" --port "$PGPORT" --username "$PGUSER" --dbname "$PGDATABASE" -f /schema_files/03_accounting_local_tables.sql\n# Nếu có thêm file SQL cho kế toán, thêm chúng ở đây\n\n>&2 echo "Accounting schema initialization complete." 